mapper.select:
select * from dataBase

    isdeleted = 0

    and userId = #{userId}


    and driver like concat('% ',#{driver},'%')


    and url like concat('%',#{url},'%')


    and userName like concat('%',#{userName},'%')


    and initialSize = #{initialSize}


    and maxActive = #{maxActive}


    and maxIdle = #{maxIdle}


    and minIdle = #{minIdle}


    and maxWait = #{maxWait}
